import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Card,
    CardContent,
    Chip,
    Container,
    Typography,

} from '@mui/material';
import ApptDialog from './Dialog/ApptDialog';

function PatientsBooking() {
    const [doctorsAvailability, setDoctorsAvailability] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedStartTimes, setSelectedStartTimes] = useState([]);
    const [selectedStartTime, setSelectedStartTime] = useState(null);
    const [dialogOpen, setDialogOpen] = useState(false);
    const [doctorUniqId, setDoctorUniqId] = useState(null);

    console.log(doctorUniqId, "sie");


    const { id } = JSON.parse(sessionStorage.getItem("user"));
    console.log(id, "ok");


    useEffect(() => {
        axios.get('http://localhost:5000/api/v3/getAvailability')
            .then(response => {
                setDoctorsAvailability(response.data);
            })
            .catch(error => {
                console.error('Error fetching doctors availability:', error);
            });
    }, []);

    const handleBooking = (doctorUniqId, id) => {
        // if (selectedDate && selectedStartTime) {
            // Format selectedDate to "YYYY-MM-DD"
            const formattedDate = selectedDate?.toISOString().split('T')[0];
    
            // Format selectedStartTime to "HH:mm"
            const formattedTime = selectedStartTime.toLocaleTimeString('en-US', {
                hour12: false,
                hour: '2-digit',
                minute: '2-digit'
            });
    
            // Combine formattedDate and formattedTime to create the timeslot string
            const timeslot = `${formattedDate} ${formattedTime}:00`;
    
            const requestData = {
                doctorId: doctorUniqId,
                patientId: id,
                timeslot: timeslot,
            };
    
            console.log(requestData, "requestData");
    
            axios.post('http://localhost:5000/api/v3/bookAppointment', requestData)
                .then(response => {
                    console.log('Appointment booked successfully:', response.data.message);
                    // Handle UI updates after successful booking if needed.
                })
                .catch(error => {
                    console.error('Error booking appointment:', error);
                    // Handle error scenario, update UI accordingly.
                });
        
    };
    

    const handleChipClick = (date, startTimes, doctorId) => {
        setSelectedDate(date);
        setSelectedStartTimes(startTimes);
        setDoctorUniqId(doctorId);
        setSelectedStartTime(null); // Reset selected start time
        setDialogOpen(true);
    };

    const handleCloseDialog = () => {
        setDialogOpen(false);
    };

    return (
        <Container maxWidth="md">
            <div>
                {doctorsAvailability.map(doctor => (
                    <div key={doctor.doctorName} className="mb-4">
                        <Card style={{ height: '100%' }}>
                            <CardContent>
                                <div className="d-flex justify-content-between mb-3">
                                    <div>
                                        <Typography variant="h6">Dr. {doctor.doctorName}</Typography>
                                        <Typography variant="body1">{doctor.specialist}</Typography>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-xl-12">
                                        {doctor.availability.map((availabilityItem, index) => (
                                            <Chip
                                                key={index}
                                                label={new Date(availabilityItem.dates[0]).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                                                onClick={() => handleChipClick(availabilityItem.dates[0], availabilityItem.startTimes, doctor.doctorId)}
                                                variant="outlined"
                                                color="primary"
                                                className="mt-2"
                                            />
                                        ))}
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                ))}
            </div>
            <ApptDialog
                dialogOpen={dialogOpen}
                selectedDate={selectedDate}
                setSelectedStartTime={setSelectedStartTime}
                selectedStartTimes={selectedStartTimes}
                selectedStartTime={selectedStartTime}
                doctorUniqId={doctorUniqId}
                id={id}
                handleCloseDialog={handleCloseDialog}
                handleBooking={handleBooking} />

        </Container>
    );
}

export default PatientsBooking;
