import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AppointmentBookingForm() {
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState('');

  useEffect(() => {
    // Fetch doctors' data from the API
    axios.get('http://localhost:5000/api/doctors')
      .then((response) => {
        setDoctors(response.data);
      })
      .catch((error) => {
        console.error('Error fetching doctors:', error);
      });
  }, []);

  const generateHourlySlots = (startTime, endTime) => {
    const slots = [];
    const currentTime = new Date(startTime);
    const endTimeObj = new Date(endTime);

    while (currentTime <= endTimeObj) {
      slots.push(currentTime.toISOString());
      currentTime.setHours(currentTime.getHours() + 1);
    }

    return slots;
  };

  const handleDoctorChange = (event) => {
    const doctorId = event.target.value;
    setSelectedDoctor(doctorId);

    // Fetch doctor's start and end times from the API
    axios.get(`http://localhost:5000/api/doctors/${doctorId}/schedule`)
      .then((response) => {
        const startTime = new Date(response.data.startTime);
        const endTime = new Date(response.data.endTime);
        const slots = generateHourlySlots(startTime, endTime);
        setAvailableSlots(slots);
      })
      .catch((error) => {
        console.error('Error fetching doctor schedule:', error);
      });
  };

  const handleSlotChange = (event) => {
    setSelectedSlot(event.target.value);
  };

  const handleAppointmentBooking = () => {
    // Implement appointment booking logic
    // ... API call and booking code here
  };

  return (
    <div>
      <h2>Book an Appointment</h2>
      <select value={selectedDoctor} onChange={handleDoctorChange}>
        <option value="">Select a doctor</option>
        {doctors.map((doctor) => (
          <option key={doctor.id} value={doctor.id}>
            {doctor.name}
          </option>
        ))}
      </select>
      <select value={selectedSlot} onChange={handleSlotChange}>
        <option value="">Select a time slot</option>
        {availableSlots.map((slot) => (
          <option key={slot} value={slot}>
            {new Date(slot).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </option>
        ))}
      </select>
      <button onClick={handleAppointmentBooking}>Book Appointment</button>
    </div>
  );
}

export default AppointmentBookingForm;
