import React, { useState, useEffect } from 'react';
import axios from 'axios';
import moment from "moment";

import {
  Card,
  CardContent,
  Chip,
  Container,
  Dialog,
  DialogContent,
  DialogTitle,
  Grid,
  Typography,
} from '@mui/material';

function PatientsBooking() {
  const [doctorsAvailability, setDoctorsAvailability] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedStartTimes, setSelectedStartTimes] = useState([]);
  const [dialogOpen, setDialogOpen] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/api/v3/getAvailability')
      .then(response => {
        setDoctorsAvailability(response.data);
      })
      .catch(error => {
        console.error('Error fetching doctors availability:', error);
      });
  }, []);

  const handleChipClick = (date, startTimes) => {
    setSelectedDate(date);
    setSelectedStartTimes(startTimes);
    setDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setDialogOpen(false);
  };

  return (
    <Container maxWidth="md">
  <div>
        {doctorsAvailability.map(doctor => (
          <div key={doctor.doctorName} className="mb-4">
            <Card style={{ height: '100%' }}>
              <CardContent>
                <div className="d-flex justify-content-between mb-3">
                  <div>
                    <Typography variant="h6">Dr.{doctor.doctorName}</Typography>
                    <Typography variant="body1">{doctor.specialist}</Typography>
                  </div>
                  <div>
                    <Dialog open={dialogOpen} onClose={handleCloseDialog}>
                      <DialogTitle>Selected Date: {selectedDate}</DialogTitle>
                      <DialogContent>
                        <Typography variant="body2">Available Start Times:</Typography>
                        {selectedStartTimes.map((startTime, index) => (
                          <Chip key={index} label={startTime} variant="outlined" color="primary" className="mt-2" />
                        ))}
                      </DialogContent>
                    </Dialog>
                  </div>
                </div>
                <div className="row">
                  <div className="col-xl-12">
                    {doctor.availability.map((availabilityItem, index) => (
                      <Chip
                      style={{
                        background: moment().format('MMM D') === moment(slot.date).format('MMM D') ? 'white' : 'yellow',
                    }}
                        key={index}
                        label={new Date(availabilityItem.dates[0]).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                        onClick={() => handleChipClick(availabilityItem.dates[0], availabilityItem.startTimes)}
                        variant="outlined"
                        color="primary"
                        className="mt-2"
                      />
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        ))}
      </div>
      <Dialog open={dialogOpen} onClose={handleCloseDialog}>
        <DialogTitle>Selected Date: {selectedDate}</DialogTitle>
        <DialogContent>
          <Typography variant="body2">Available Start Times:</Typography>
          {selectedStartTimes.map((startTime, index) => (
            <Chip key={index} label={startTime} variant="outlined" color="primary" className="mt-2" />
          ))}
        </DialogContent>
      </Dialog>
    </Container>
  );
}

export default PatientsBooking;
