import React, { useEffect, useState } from 'react';
import {
    Dialog,
    DialogContent,
    DialogTitle,
    Typography,
    Chip,
    Avatar,
    IconButton,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

export default function ApptDialog({ doctorUniqId, id, dialogOpen, handleCloseDialog, selectedDate, doctorsAvailability, setSelectedStartTime, handleBooking }) {


    
    const convertTo12HourFormat = (time) => {
        const [hours, minutes] = time.split(":");
        const parsedHours = parseInt(hours, 10);
        const ampm = parsedHours >= 12 ? "PM" : "AM";
        const twelveHour = parsedHours % 12 || 12;
        return `${twelveHour}:${minutes} ${ampm}`;
    };

    const handleNextDays = () => {
        setStartDate(new Date(startDate.getTime() + 7 * 24 * 60 * 60 * 1000)); // 7 days later
        setEndDate(new Date(endDate.getTime() + 7 * 24 * 60 * 60 * 1000)); // 7 days later
    };

    const handlePreviousDays = () => {
        setStartDate(new Date(startDate.getTime() - 7 * 24 * 60 * 60 * 1000)); // 7 days earlier
        setEndDate(new Date(endDate.getTime() - 7 * 24 * 60 * 60 * 1000)); // 7 days earlier
    };

    return (
        <div>
            <Dialog open={dialogOpen} onClose={handleCloseDialog} fullWidth>
                <DialogTitle style={{ margin: "1rem", borderBottom: "2px grey solid", display: "flex", alignItems: "center" }}>
                    {/* Avatar and doctor information */}
                </DialogTitle>

                <DialogContent style={{ margin: "1rem" }}>
                    {selectedDoctorInfo && selectedDoctorInfo.availability.map((availabilityItem, index) => {
                        const slotDate = new Date(availabilityItem.dates[0]);

                        if (slotDate >= startDate && slotDate <= endDate) {
                            return (
                                <div key={index}>
                                    <Typography style={{ fontSize: '15px' }} variant="body2">
                                        {slotDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                                    </Typography>
                                    {getAvailableSlotsForDate(availabilityItem.dates[0]).map((startTime, slotIndex) => (
                                        <Chip
                                            style={{ padding: "0.5rem", fontSize: "15px", borderRadius: "2px", margin: '0.3rem', background: "yellow" }}
                                            key={slotIndex}
                                            label={convertTo12HourFormat(startTime)} // Convert time to 12-hour format
                                            className="mt-2"
                                            onClick={() => {
                                                setSelectedStartTime(startTime);
                                                handleBooking(doctorUniqId, id, availabilityItem.dates[0]);
                                            }}
                                        />
                                    ))}
                                </div>
                            );
                        } else {
                            return null; // Do not render slots outside the date range
                        }
                    })}
                </DialogContent>

                <div style={{ display: "flex", alignItems: "center", justifyContent: "center", marginTop: "1rem" }}>
                    <IconButton onClick={handlePreviousDays}>
                        <ArrowBackIcon />
                    </IconButton>
                    <Typography style={{ fontWeight: "bold", fontSize: "16px" }}>
                        {`${startDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} to ${endDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`}
                    </Typography>
                    <IconButton onClick={handleNextDays}>
                        <ArrowForwardIcon />
                    </IconButton>
                </div>
            </Dialog>
        </div>
    );
}
