import React, { useEffect, useState } from 'react';
import {
    Dialog,
    DialogContent,
    DialogTitle,
    Typography,
    Chip,
} from '@mui/material';
import "./AppDialog.css";


export default function ApptDialog({ recentClickedDate, doctorUniqId, id, dialogOpen, handleCloseDialog, selectedDate, doctorsAvailability, setSelectedStartTime, handleBooking }) {
    const [selectedDoctorInfo, setSelectedDoctorInfo] = useState(null);

    useEffect(() => {
        const doctorInfo = doctorsAvailability.find(doctor => doctor.doctorId === doctorUniqId);
        setSelectedDoctorInfo(doctorInfo);
    }, [doctorsAvailability, doctorUniqId]);

    const getAvailableSlotsForDate = (date) => {
        if (selectedDoctorInfo) {
            const availabilityItem = selectedDoctorInfo.availability.find(item => item.dates[0] === date);
            return availabilityItem ? availabilityItem.startTimes : [];
        }
        return [];
    };

    const filteredAvailability = selectedDoctorInfo
        ? selectedDoctorInfo.availability.filter(item => new Date(item.dates[0]) >= new Date(recentClickedDate))
        : [];

    return (
        <div>
            <Dialog open={dialogOpen} onClose={handleCloseDialog} minWidth="md" fullWidth>
                <DialogTitle style={{ fontSize: '17px', fontWeight: "550" }}>
                    {selectedDoctorInfo ? `Dr. ${selectedDoctorInfo.doctorName}` : "Doctor Name"}
                </DialogTitle>
                <DialogContent style={{ marginTop: '-1rem' }}>
                    <div className="sticky-container">
                        <Typography style={{ fontSize: '16px' }} variant="body2">
                            {selectedDoctorInfo ? selectedDoctorInfo.specialist : "Specialist"}
                        </Typography>
                        <Typography variant="body2" style={{ color: "grey" }}>33 N Fullerton Ave Montclair NJ 07042</Typography>
                        <Typography variant="body2" style={{ color: "grey" }}>Available Start Times:</Typography>
                    </div>
                    {filteredAvailability.map((availabilityItem, index) => (
                        <div key={index}>
                            <Typography style={{ fontSize: '15px' }} variant="body2">
                                {new Date(availabilityItem.dates[0]).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                            </Typography>
                            {getAvailableSlotsForDate(availabilityItem.dates[0]).map((startTime, slotIndex) => (
                            <Chip
                            style={{ padding: "1rem", fontSize: "15px", borderRadius: "2px", margin: '0.3rem', background: "yellow" }}
                            key={slotIndex}
                            label={startTime}
                            className="mt-2"
                            onClick={() => {
                                console.log("Clicked date:", availabilityItem.dates[0]); // Add this line
                                setSelectedStartTime(startTime);
                                handleBooking(doctorUniqId, id, availabilityItem.dates[0]);
                            }}
                        />
                        
                            ))}
                        </div>
                    ))}
                </DialogContent>
            </Dialog>
        </div>
    );
}
