import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Paper, Grid, TextField, Checkbox, Button, Select, MenuItem, FormControlLabel } from '@mui/material';

const BackendBaseUrl = 'http://localhost:5000'; // Update with your backend server URL

function DoctorSignupoi() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [licenseNumber, setLicenseNumber] = useState('');
  const [specialist, setSpecialist] = useState('');
  const [signupMessage, setSignupMessage] = useState('');
  const [isMale, setIsMale] = useState(false);
  const [isFemale, setIsFemale] = useState(false);

  const [getData, setData] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/v1/specialties")
      .then((res) => {
        setData(res.data)
      })
  }, []);

  const handleSignup = () => {
    // Determine the sex based on checkboxes
    const sexValue = isMale ? 'M' : (isFemale ? 'F' : '');

    axios
      .post('http://localhost:5000/api/v1/doc', {
        gender: sexValue,
        name,
        email,
        password,
        medical_license_number: licenseNumber,
        specialist,
      })
      .then(response => {
        setSignupMessage(response.data); // Success message
      })
      .catch(error => {
        console.error('Error signing up:', error);
        setSignupMessage('Error signing up');
      });
  };

  return (
    <Container maxWidth="sm" className='mt-4'>
      <Paper elevation={3} sx={{ padding: 2 }}>
        <h2>Doctor Signup</h2>
        <TextField label="Name" className='mt-2' fullWidth value={name} onChange={(e) => setName(e.target.value)} />
        <TextField label="Email" className='mt-2' fullWidth value={email} onChange={(e) => setEmail(e.target.value)} />
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <FormControlLabel
              control={<Checkbox checked={isMale} onChange={() => { setIsMale(!isMale); setIsFemale(false); }} />}
              label="Male"
            />
          </Grid>
          <Grid item xs={6}>
            <FormControlLabel
              control={<Checkbox checked={isFemale} onChange={() => { setIsFemale(!isFemale); setIsMale(false); }} />}
              label="Female"
            />
          </Grid>
        </Grid>
        <TextField label="Medical License Number" className='mt-2'  fullWidth value={licenseNumber} onChange={(e) => setLicenseNumber(e.target.value)} />
        <Select className='mt-2'value={specialist} onChange={(e) => setSpecialist(e.target.value)} fullWidth>
          <MenuItem value="">Select a specialist</MenuItem>
          {getData.map(specialty => (
            
            <MenuItem key={specialty} value={specialty}>{specialty}</MenuItem>
            
          ))}
        </Select>
        <TextField label="Password" className='mt-2' fullWidth value={password} onChange={(e) => setPassword(e.target.value)} />

        <Button className='mt-4' variant="contained" color="primary" onClick={handleSignup}>Sign Up</Button>
        <p>{signupMessage}</p>
      </Paper>
    </Container>
  );
}

export default DoctorSignup;
