import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Card,
    CardContent,
    Chip,
    Container,
    Typography,
} from '@mui/material';
import ApptDialog from './Dialog/ApptDialog';

function PatientsBooking() {
    const [doctorsAvailability, setDoctorsAvailability] = useState([]);
    const [dialogOpen, setDialogOpen] = useState(false);
    const [selectedDoctorId, setSelectedDoctorId] = useState(null);
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedStartTime, setSelectedStartTime] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:5000/api/v3/getAvailability')
            .then(response => {
                setDoctorsAvailability(response.data);
            })
            .catch(error => {
                console.error('Error fetching doctors availability:', error);
            });
    }, []);

    const handleBooking = (doctorId, patientId, timeslot) => {
        // Perform booking logic here using axios.post
        // Handle UI updates after successful booking if needed.
    };

    const handleChipClick = (doctorId, date, startTimes) => {
        setSelectedDoctorId(doctorId);
        setSelectedDate(date);
        setSelectedStartTime(null); // Reset selected start time
        setDialogOpen(true);
    };

    const handleCloseDialog = () => {
        setDialogOpen(false);
    };

    return (
        <Container maxWidth="md">
            <div>
                {doctorsAvailability.map(doctor => (
                    <div key={doctor.doctorId} className="mb-4">
                        <Card style={{ height: '100%' }}>
                            <CardContent>
                                <div className="d-flex justify-content-between mb-3">
                                    <div>
                                        <Typography variant="h6">Dr. {doctor.doctorName}</Typography>
                                        <Typography variant="body1">{doctor.specialist}</Typography>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-xl-12">
                                        {doctor.availability.map((availabilityItem, index) => (
                                            <Chip
                                                key={index}
                                                label={new Date(availabilityItem.date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                                                onClick={() => handleChipClick(doctor.doctorId, availabilityItem.date, availabilityItem.startTimes)}
                                                className="mt-2"
                                            />
                                        ))}
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                ))}
            </div>
            <ApptDialog
                dialogOpen={dialogOpen}
                selectedDate={selectedDate}
                setSelectedStartTime={setSelectedStartTime}
                selectedStartTime={selectedStartTime}
                selectedDoctorId={selectedDoctorId}
                handleCloseDialog={handleCloseDialog}
                doctorsAvailability={doctorsAvailability}
                handleBooking={handleBooking}
            />
        </Container>
    );
}

export default PatientsBooking;
