import React, { useState, useEffect } from 'react';
import { Card, CardContent, Chip, Container, Dialog, DialogContent, DialogTitle, Grid, Typography } from '@mui/material';

function Slots({ availability, doctorAppointments, formatAppointmentTime }) {
    const [selectedSlot, setSelectedSlot] = useState(null);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedPatientTime, setSelectedPatientTime] = useState(null);


    const handleClickChip = (slot) => {
        setSelectedSlot(slot);
        setOpenDialog(true);

        const formattedSlot = (slot); // Format slot to match booktime format
        const matchingAppointment = doctorAppointments?.find(appointment =>
            appointment.booktime === formattedSlot

        );
        console.log('Formatted Slot:', formattedSlot);
        console.log('Matching Appointment:', matchingAppointment);
        console.log('Appointment Times:', doctorAppointments?.map(appointment => appointment.booktime === formattedSlot));

        setSelectedPatientTime(matchingAppointment ? (matchingAppointment.booktime) : 'No appointment booked');
    };

    const handleCloseDialog = () => {
        setSelectedSlot(null);
        setSelectedPatientTime(null);
        setOpenDialog(false);
    };

    return (
        <Container maxWidth="md">
            <Grid container spacing={3}>
                {availability.map((item, index) => (
                    <Grid key={index} item xs={12} sm={6} md={11}>
      
                        <Card style={{ height: '100%' }}>
                            <CardContent>
                                <h6 align="left">Date: {item.dates[0]}</h6>
                                <p style={{ color: 'grey', fontSize: '13px' }} align="left">
                                    Upcoming Appointments
                                </p>
                                <div a>
                                    {item.startTimes.map((startTime, timeIndex) => {
                                        // console.log(timeIndex);
                                        const formattedStartTime = (startTime); // Format startTime to match booktime format
                                        const isBooked = doctorAppointments.some(appointment =>
                                            appointment.booktime === formattedStartTime
                                        );

                                        return (
                                            <Chip
                                            style={{ padding: "0.5rem", fontSize: "15px", borderRadius: "2px", margin: '0.3rem', background:  isBooked ? "#2196f3" : "yellow" }}

                                                key={timeIndex}
                                                label={startTime}
                                                variant={isBooked ? 'secondary' : 'outlined'}
                                                color={isBooked ? 'primary' : 'default'}
                                                className="mt-2"
                                                onClick={() => handleClickChip(startTime)}
                                            />
                                        );
                                    })}
                                </div>
                            </CardContent>
                        </Card>
             
                    </Grid>
                ))}
            </Grid>
            <Dialog open={openDialog} onClose={handleCloseDialog}>
                <DialogTitle>Selected Slot Information</DialogTitle>
                <DialogContent>
                    <Typography>
                        {selectedSlot ? `You clicked on the slot: ${selectedSlot}` : 'No slot selected'}
                        <br />
                        {selectedPatientTime && (
                            <div>
                                {selectedPatientTime === 'No appointment booked'
                                    ? selectedPatientTime
                                    : `Patient Booking Time: ${selectedPatientTime}`}
                            </div>
                        )}
                    </Typography>

                </DialogContent>
            </Dialog>

        </Container>
    );
}

export default Slots;
