import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Container, TextField, Typography, Grid, Dialog, DialogTitle, DialogContent } from '@mui/material';
import Slots from './Slots';
import { injectStyle } from 'react-toastify/dist/inject-style';
import { ToastContainer, toast } from 'react-toastify';

if (typeof window !== 'undefined') {
  injectStyle();
}

function DoctorHome() {
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [message, setMessage] = useState('');
  const [availability, setAvailability] = useState([]);
  const { id } = JSON.parse(sessionStorage.getItem('user'));

  const [doctorAppointments, setDoctorAppointments] = useState([]);
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  useEffect(() => {
    // Make the GET request using axios
    axios
      .get(`http://localhost:5000/api/v3/getDoctorAppointments?doctorId=${id}`)
      .then(response => {
        setDoctorAppointments(response.data.doctorAppointments);
      })
      .catch(error => {
        console.error('Error fetching doctor appointments:', error);
      });
  }, [id]);

  const formatAppointmentTime = dateTimeString => {
    const options = {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true,
    };
    return new Date(dateTimeString).toLocaleString('en-US', options);
  };

  const handleSetAvailability = () => {
    const payload = {
      doctorId: id,
      date: date,
      startTime: startTime,
      endTime: endTime,
    };

    axios
      .post('http://localhost:5000/api/v3/setAvailability', payload)
      .then(response => {
        toast.success(response.data.message);
        fetchAvailability();
      })
      .catch(error => {
        console.error('Error setting availability:', error);
        toast.warning(error.response.data.message);
      });
  };

  const fetchAvailability = () => {
    axios
      .get(`http://localhost:5000/api/v3/getAvailability/${id}`)
      .then(response => {
        setAvailability(response.data);
      })
      .catch(error => {
        console.error('Error fetching availability:', error);
      });
  };

  useEffect(() => {
    fetchAvailability();
  }, [id]);

  const handleOpenDialog = appointment => {
    setSelectedAppointment(appointment);
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setSelectedAppointment(null);
    setIsDialogOpen(false);
  };

  return (
    <>
      <Container maxWidth="sm">
        <Typography variant="h4" gutterBottom>
          Set Doctor Availability
        </Typography>
        <ToastContainer />
        <Grid container spacing={2}>
          {/* ... Other input fields */}
        </Grid>

        <Button variant="contained" color="primary" className="mt-3" onClick={handleSetAvailability}>
          Set Availability
        </Button>

        <Dialog open={isDialogOpen} onClose={handleCloseDialog}>
          <DialogTitle>Appointment Details</DialogTitle>
          <DialogContent>
            {selectedAppointment && (
              <>
                <Typography>Appointment Time: {formatAppointmentTime(selectedAppointment.appointmentTime)}</Typography>
                {/* Display other appointment details here */}
              </>
            )}
          </DialogContent>
        </Dialog>
      </Container>
      <div className="mt-5">
        <Slots availability={availability} doctorAppointments={doctorAppointments} />
      </div>
    </>
  );
}

export default DoctorHome;
